import React from 'react';
// import Container from 'react-bootstrap/Container';
import Axios from 'axios';

import BlogPost from '../components/BlogPost';
import Hero from '../components/Hero';

class BlogPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            posts: [

            ]
        }
    }

    // componentDidMount() {
    //     do {
    //         this.buildPosts();
    //         i++;
    //     } while (i < 1);
    // }

    getPosts = () => {
        //Get Request to '/posts/'
        Axios.get('http://localhost:5000/blog/posts/', this.state)
            .then(res => {
                this.setState({ posts: res.data });
                console.log(this.state.posts);
            });

        //res.json() => setState(posts)
        //foreach(post in posts) => new BlogPost
    }

    render() {
        window.onload(alert('this worked'));
        console.log(Axios.get('http://localhost:5000/blog/posts/'));
        if (this.state.posts.length !== 0) {
            return (
                <div>
                    <Hero title={this.props.title} />
                    {this.state.posts.map(post => (
                        <div>
                            <BlogPost title={post.postTitle} content={post.postContent} date={post.postDate} key={post.id} />
                        </div>
                    ))}
                </div>
            );
        } else {
            return (
                <div>
                    <Hero title={this.props.title} />
                    <div>Check back later for new posts</div>
                </div>
            );
        }
    }
}

export default BlogPage;
